import React, { useState } from "react";
import "../css/base.css";
import "../css/drinks.css";

function Menu() {
  const [cart, setCart] = useState([]);

  const handleAddToCart = (item) => {
    setCart((prevCart) => [...prevCart, item]);
  };

  const menuSections = [
    {
      title: "Hot Coffees",
      items: [
        { name: "Espresso", description: "Rich, intense shot of pure coffee.", price: "KSh 200", img: "https://images.unsplash.com/photo-1509042239860-f550ce710b93" },
        { name: "Cappuccino", description: "Espresso with steamed milk and thick foam.", price: "KSh 300", img: "https://images.unsplash.com/photo-1511920170033-f8396924c348" },
        { name: "Café Latte", description: "Smooth espresso blended with steamed milk.", price: "KSh 320", img: "https://images.unsplash.com/photo-1559049936-39844e0f8227" },
        { name: "Macchiato", description: "Espresso topped with a dollop of milk foam.", price: "KSh 280", img: "https://images.unsplash.com/photo-1509042239860-f550ce710b93" },
        { name: "Mocha", description: "Espresso, chocolate, and steamed milk with whipped cream.", price: "KSh 350", img: "https://images.unsplash.com/photo-1523942839745-7848d4a6a10f" },
      ],
    },
    {
      title: "Iced & Cold Coffees",
      items: [
        { name: "Iced Americano", description: "Espresso mixed with cold water and ice.", price: "KSh 250", img: "https://images.unsplash.com/photo-1504674900247-0877df9cc836" },
        { name: "Cold Brew", description: "Slow-steeped coffee for a naturally sweet flavor.", price: "KSh 320", img: "https://images.unsplash.com/photo-1498804103079-a6351b050096" },
        { name: "Iced Latte", description: "Cold espresso with chilled milk and ice cubes.", price: "KSh 300", img: "https://images.unsplash.com/photo-1541167760496-1628856ab772" },
        { name: "Caramel Iced Coffee", description: "Sweet caramel syrup with cold espresso and milk.", price: "KSh 340", img: "https://images.unsplash.com/photo-1510626176961-4b57d4fbad03" },
        { name: "Frappuccino", description: "Blended coffee ice drink topped with whipped cream.", price: "KSh 400", img: "https://images.unsplash.com/photo-1510626176961-4b57d4fbad03" },
      ],
    },
    {
      title: "Teas & Infusions",
      items: [
        { name: "Green Tea", description: "Lightly brewed, antioxidant-rich.", price: "KSh 200", img: "https://images.unsplash.com/photo-1505576399279-565b52d4ac61" },
        { name: "Masala Chai", description: "Spiced Kenyan black tea with milk and herbs.", price: "KSh 250", img: "https://images.unsplash.com/photo-1578683010236-d716f9a3f461" },
        { name: "Lemon Ginger Tea", description: "Refreshing and soothing herbal tea.", price: "KSh 220", img: "https://images.unsplash.com/photo-1561043433-aaf687c4cf4e" },
        { name: "Iced Hibiscus Tea", description: "Tart, floral, and served cold.", price: "KSh 260", img: "https://images.unsplash.com/photo-1544025162-d76694265947" },
        { name: "Mint Tea", description: "Hot infusion of mint leaves and honey.", price: "KSh 240", img: "https://images.unsplash.com/photo-1600271886473-2a4e3fda8e4b" },
      ],
    },
    {
      title: "Finger Foods",
      items: [
        { name: "Croissant", description: "Flaky buttery French pastry.", price: "KSh 200", img: "https://images.unsplash.com/photo-1509440159596-0249088772ff" },
        { name: "Blueberry Muffin", description: "Soft muffin packed with blueberries.", price: "KSh 250", img: "https://images.unsplash.com/photo-1509440159596-0249088772ff" },
        { name: "Chicken Sandwich", description: "Grilled chicken with lettuce and mayo.", price: "KSh 450", img: "https://images.unsplash.com/photo-1586816001966-79b736744398" },
        { name: "Veggie Wrap", description: "Fresh vegetables in a tortilla wrap.", price: "KSh 400", img: "https://images.unsplash.com/photo-1590080875831-cafca0c3d13e" },
        { name: "Cookies (2 pcs)", description: "Soft baked chocolate chip cookies.", price: "KSh 180", img: "https://images.unsplash.com/photo-1608198093002-ad4e005484fa" },
      ],
    },
  ];

  return (
    <section className="menu-section">
      <h2 className="menu-title">Our Menu</h2>
      <p className="intro-text">
        From handcrafted coffees to refreshing teas and delicious snacks — enjoy the best of Café Bliss!
      </p>

      <div className="menu-container">
        {menuSections.map((section, index) => (
          <div key={index} className="menu-category">
            <h3>{section.title}</h3>
            <div className="menu-items">
              {section.items.map((item, i) => (
                <div className="menu-item" key={i}>
                  <img src={item.img} alt={item.name} className="menu-img" />
                  <div className="menu-item-details">
                    <h4>{item.name}</h4>
                    <p>{item.description}</p>
                    <div className="menu-bottom">
                      <span className="price">{item.price}</span>
                      <button
                        className="order-btn"
                        onClick={() => handleAddToCart(item)}
                      >
                        Add to Order
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {/* Floating Cart Sidebar */}
      <div className="cart-sidebar">
        <h4>Your Order</h4>
        {cart.length === 0 ? (
          <p>No items yet.</p>
        ) : (
          <ul>
            {cart.map((item, i) => (
              <li key={i}>
                {item.name} <span>{item.price}</span>
              </li>
            ))}
          </ul>
        )}
        {cart.length > 0 && (
          <p className="total">
            Total:{" "}
            <strong>
              KSh{" "}
              {cart
                .reduce(
                  (sum, item) => sum + parseInt(item.price.replace("KSh ", "")),
                  0
                )
                .toLocaleString()}
            </strong>
          </p>
        )}
      </div>
    </section>
  );
}

export default Menu;
