import React from "react";
import "../css/base.css";
import "../css/drinks.css";

function Menu() {
  const menuSections = [
    {
      title: "Hot Coffees",
      items: [
        { name: "Espresso", description: "Rich, intense shot of pure coffee.", price: "KSh 200" },
        { name: "Cappuccino", description: "Espresso with steamed milk and thick foam.", price: "KSh 300" },
        { name: "Café Latte", description: "Smooth espresso blended with steamed milk.", price: "KSh 320" },
        { name: "Macchiato", description: "Espresso topped with a small dollop of milk foam.", price: "KSh 280" },
        { name: "Mocha", description: "Espresso, chocolate, and steamed milk with whipped cream.", price: "KSh 350" },
      ],
    },
    {
      title: "Iced & Cold Coffees",
      items: [
        { name: "Iced Americano", description: "Espresso mixed with cold water and ice.", price: "KSh 250" },
        { name: "Cold Brew", description: "Slow-steeped coffee for a naturally sweet flavor.", price: "KSh 320" },
        { name: "Iced Latte", description: "Cold espresso with chilled milk and ice cubes.", price: "KSh 300" },
        { name: "Caramel Iced Coffee", description: "Sweet caramel syrup with cold espresso and milk.", price: "KSh 340" },
        { name: "Frappuccino", description: "Blended coffee ice drink topped with whipped cream.", price: "KSh 400" },
      ],
    },
    {
      title: "Teas & Infusions",
      items: [
        { name: "Green Tea", description: "Lightly brewed, healthy, and antioxidant-rich.", price: "KSh 200" },
        { name: "Masala Chai", description: "Spiced Kenyan black tea with milk and herbs.", price: "KSh 250" },
        { name: "Lemon Ginger Tea", description: "Refreshing and soothing herbal tea.", price: "KSh 220" },
        { name: "Iced Hibiscus Tea", description: "Tart, floral, and served cold.", price: "KSh 260" },
        { name: "Mint Tea", description: "Hot infusion of mint leaves and honey.", price: "KSh 240" },
      ],
    },
    {
      title: "Finger Foods",
      items: [
        { name: "Croissant", description: "Flaky buttery French pastry.", price: "KSh 200" },
        { name: "Blueberry Muffin", description: "Moist muffin packed with fresh blueberries.", price: "KSh 250" },
        { name: "Chicken Sandwich", description: "Grilled chicken with lettuce and mayo.", price: "KSh 450" },
        { name: "Veggie Wrap", description: "Fresh vegetables in a soft tortilla wrap.", price: "KSh 400" },
        { name: "Cookies (2 pcs)", description: "Soft baked chocolate chip cookies.", price: "KSh 180" },
      ],
    },
  ];

  return (
    <section className="menu-section">
      <h2 className="menu-title">Our Menu</h2>
      <p className="intro-text">
        From handcrafted coffees to refreshing teas and quick bites — enjoy the best of Café Bliss!
      </p>

      {menuSections.map((section, index) => (
        <div key={index} className="menu-category">
          <h3>{section.title}</h3>
          <div className="menu-items">
            {section.items.map((item, i) => (
              <div className="menu-item" key={i}>
                <div className="menu-item-text">
                  <h4>{item.name}</h4>
                  <p>{item.description}</p>
                </div>
                <span className="price">{item.price}</span>
              </div>
            ))}
          </div>
        </div>
      ))}
    </section>
  );
}

export default Menu;
